static char* collections_strndup(const char *string, size_t n) {
    char *output_string = (char*)collections_malloc(n + 1, ape_mallocArg);
    if (!output_string) {
        return NULL;
    }
    output_string[n] = '\0';
    memcpy(output_string, string, n);
    return output_string;
}

